//some common vars (these could be passed in from another node)
var position = msg.payload.position || {  x : 10, y: 10 }
var padding = msg.payload.padding || { x: 2, y: 2 };
var text = msg.payload.text;

var icon = msg.icon; //get icon loaded by previous node


//create the batch operations
msg.batch = [];

if(icon){
    //op1 blt an icon onto image
    var op1 = {
        "name":"blit",
        "parameters":[
            icon,
            position.x, //dst x
            position.y, //dst y
            0, //src x
            0, //src y
            20 , //src w
            20   //src h
        ]
    }
    position.x += icon.bitmap.width + padding.x;
    msg.batch.push(op1);
}

//op2 print text next to icon
var op2 = {
    "name": "print",
    "parameters": [
        "FONT_SANS_16_BLACK",
        position.x, //x
        position.y + padding.y + 2, //y
        {
            "text": text,
            "alignmentX": "Jimp.HORIZONTAL_ALIGN_LEFT",
            "alignmentY": "Jimp.VERTICAL_ALIGN_TOP"
        },
        -1,
        -1
    ]
}
msg.batch.push(op2)

return msg;
